package com.ksv.producer.kafka; import org.apache.kafka.clients.admin.*; import org.springframework.beans.factory.annotation.*; import org.springframework.context.annotation.*; @Configuration public class KafkaTopicsConfig { @Value("${app.topics.ticket-created}") private String created; @Value("${app.topics.ticket-created-dlt}") private String dlt; @Bean NewTopic t1(){ return new NewTopic(created,3,(short)1);} @Bean NewTopic t2(){ return new NewTopic(dlt,3,(short)1);} }